mapper.update:
update car_reservation
set ap_status =#{apStatus}
,approver=#{approver}
,ap_date = SYSDATE
,reject=#{reject}
where res_no=#{resNo}

	update car
       set CAR_TYPE=#{carType}
        ,GEAR_TYPE=#{gearType}
        ,CAR_COLOR =#{color}
        ,MAKER =#{maker}
        ,car_img=#{carImg}
        ,FUEL_TYPE =#{fuelType}
        ,MODEL_YEAR =#{modelYear}
        ,ETC =#{etc}
        ,STATUS =#{status}
		 where car_name= #{carName}

mapper.select:
select RES_NO ,CAR_NAME ,reservation ,REQUEST_DATE ,RES_DATE ,START_TIME ,END_TIME ,CAUSE
,ap_status from car_reservation
where ap_status=2 and car_name=#{carName} and RES_DATE=#{resDate}
