mapper.insert:
insert into
	cfr_reservation
	(
	RES_NO
	,CFR_NAME
	,RESERVATION
	,start_time
	,end_time
	,use_date
	,capacity
	,cf_title
	,status
	)
	values
	(
	SEQ_CFRNO.NEXTVAL
	,#{cfrName}
	,#{reservation}
	,#{startTime}
	,#{endTime}
	,#{useDate}
	,#{capacity}
	,#{cfTitle}
	,2
	)

mapper.update:
update
	cf_room
set CAPACITY=#{capacity}
,cfr_img=#{firstImg}
,equipment=#{equipment}
where cfr_name=#{cfrName}
