mapper.update:
update
       employee
   set email = #{email}
     , phone = #{phone}
     , emp_phone = #{empPhone}
     , birth = #{birth}
     , task = #{task}
     , modify_date = sysdate
 where emp_no = #{empNo}

update
	   employee
   set profile_url = #{profileUrl}
     , modify_date = sysdate
 where emp_no = #{empNo}
