mapper.insert:
insert 
  into leave
  (
  	leave_no
  , emp_no
  , dept_code
  , team_code
  , leave_category
  , leave_start
  , leave_end
  , leave_content
  , enroll_date
  )
  values
  (
  	seq_leno.nextval
  , #{empNo}
  , #{deptCode}
  , #{teamCode}
  , #{leaveCategory}
  , #{leaveStart}
  , #{leaveEnd}
  , #{leaveContent}
  , sysdate
  )

mapper.update:
update leave
   set leave_status = 'N'
 where leave_no = #{leaveNo}

update leave
   set leave_check = 1
     , first_date = sysdate
     , first_approval = #{firstApproval}
 where leave_no = #{leaveNo}

update leave
   set leave_check = 2
     , second_date = sysdate
     , second_approval = #{secondApproval}
 where leave_no = #{leaveNo}

